import Cookies from 'js-cookie';
var key = process.env.NEXT_PUBLIC_COOKIES_KEY;

function get(name) {
  var value = Cookies.get("".concat(key, "/").concat(name));

  if (value) {
    return JSON.parse(value);
  }

  return null;
}

function remove(name) {
  Cookies.remove("".concat(key, "/").concat(name));
}

function set(name, value) {
  return Cookies.set("".concat(key, "/").concat(name), value);
}

export default {
  get: get,
  remove: remove,
  set: set
};
