import React, { useRef } from 'react';
import { FiSearch } from 'react-icons/fi';
import { Column } from 'react-table';

import Header from '@/components/Header';
import Select from '@/components/Select';
import SEO from '@/components/SEO';
import Sidebar from '@/components/Sidebar';
import Table from '@/components/Table';
import Title from '@/components/Title';
import { Button, Flex, Text, Tooltip } from '@chakra-ui/core';
import { FormHandles } from '@unform/core';
import { Form } from '@unform/web';

interface IFormData {
  email: string;
  password: string;
}

const SUMMARY_OF_ACTIVITIES = [
  {
    Header: <Text color="#f33">Item</Text>,
    accessor: 'item',
  },
  {
    Header: 'Atividades atrasadas',
    accessor: 'late_activities',
  },
  {
    Header: '% Prev. Até a data',
    accessor: 'prev',
  },
  {
    Header: '% Real. Até a data',
    accessor: 'real',
  },
] as Column[];

const Dashboad: React.FC = () => {
  const formRef = useRef<FormHandles>(null);
  return (
    <>
      <SEO
        title="Lafe"
        image="og/boost.png"
        shouldExcludeTitleSuffix
        description="Fazer Dashboad na plataforma"
      />

      <Flex
        as="main"
        height="100vh"
        direction="column"
        backgroundColor="blue.500"
      >
        <Header />
        <Flex height="100%">
          <Sidebar />
          <Flex
            paddingX={6}
            paddingY={2}
            width="100%"
            direction="column"
            height="100%"
          >
            <Flex color="gray.800" direction="column" width="100%">
              <Title>Filtros</Title>
              <Form
                css={{
                  width: '35%',
                  display: 'flex',
                  alignItems: 'center',
                  flexDirection: 'row',
                }}
                ref={formRef}
                onSubmit={() => {}}
              >
                <Select
                  height={8}
                  backgroundColor="White"
                  name="construction"
                  placeholder="Selecione a obra"
                  containerProps={{
                    border: '1px solid',
                    height: 10,
                    borderColor: 'gray.400',
                    bg: 'white',
                  }}
                >
                  <option value="Obra1">Obra1</option>
                  <option value="Obra2">Obra2</option>
                  <option value="Obra3">Obra3</option>
                </Select>
                <Select
                  height={8}
                  backgroundColor="White"
                  name="month"
                  placeholder="Selecione o mês"
                  containerProps={{
                    height: 10,
                    marginLeft: '16px',
                    border: '1px solid',
                    borderColor: 'gray.400',
                    bg: 'white',
                  }}
                >
                  <option value="Mês1">Mês1</option>
                  <option value="Mês2">Mês2</option>
                  <option value="Mês3">Mês3</option>
                </Select>
                <Tooltip
                  label="Pesquisar velórios por cemitério"
                  aria-label="Pesquisar velórios por cemitério"
                >
                  <Button marginLeft={4} height={10} type="submit">
                    <FiSearch size={40} />
                  </Button>
                </Tooltip>
              </Form>
            </Flex>
            <Flex marginTop={4} width="50%">
              <Table
                title="Principais Atividades Resumo"
                data={[
                  {
                    item: <Text color="#f33">05</Text>,
                    late_activities: 'FUNDAÇÃO',
                    prev: '32,20%',
                    real: '17,17%',
                  },
                ]}
                columns={SUMMARY_OF_ACTIVITIES}
              />
            </Flex>
          </Flex>
        </Flex>
      </Flex>
    </>
  );
};

export default Dashboad;
