import React, { useCallback, useRef } from 'react';

import { Box, Button, Flex, useToast } from '@chakra-ui/core';
import { FormHandles } from '@unform/core';
import { Form } from '@unform/web';
import * as Yup from 'yup';

import Input from '@/components/Input';
import SEO from '@/components/SEO';
import Sidebar from '@/components/Sidebar';
import Title from '@/components/Title';
import getValidationErrors from '@/utils/getValidationErrors';

import api from '../../services/api';

interface IFormData {
  name: string;
}

const Cemeteries: React.FC = () => {
  const formRef = useRef<FormHandles>(null);

  const toast = useToast();

  const handleSubmit = useCallback(async (data: IFormData, { reset }) => {
    try {
      formRef.current?.setErrors({});

      const schema = Yup.object().shape({
        name: Yup.string().required('Nome obrigatório'),
      });

      await schema.validate(data, { abortEarly: false });

      await api.post('cemeteries', data);

      toast({
        status: 'success',
        title: 'Cemitério criado com sucesso',
        position: 'top',
        duration: 3000,
      });

      reset();
    } catch (err) {
      if (err instanceof Yup.ValidationError) {
        const errors = getValidationErrors(err);

        formRef.current?.setErrors(errors);

        return;
      }

      toast({
        status: 'error',
        title: 'Erro ao registrar cemitério',
        description: 'Ocorreu um erro ao registrar cemitério, tente novamente.',
        position: 'top',
        duration: 5000,
      });
    }
  }, []);

  return (
    <>
      <SEO
        title="Endor"
        image="og/boost.png"
        shouldExcludeTitleSuffix
        description="Fazer o registro de cemitérios na plataforma"
      />
      <Flex
        as="main"
        height="100vh"
        position="relative"
        backgroundColor="gray.800"
      >
        <Sidebar />

        <Flex
          paddingLeft={2}
          paddingTop={15}
          paddingRight={65}
          width="100%"
          height="100%"
          bg="gray.800"
          direction="column"
        >
          <Box width="100%" color="gray.200">
            <Title css={{ color: 'gray.200' }}>Cemitérios</Title>
          </Box>

          <Form
            ref={formRef}
            css={{ display: 'flex', flexDirection: 'column', height: '100%' }}
            onSubmit={handleSubmit}
          >
            <Flex flexDirection="column" height="100%">
              <Input
                name="name"
                placeholder="Nome"
                containerProps={{ width: '100%' }}
              />
              <Tooltip
                label="Pesquisar velórios por cemitério"
                aria-label="Pesquisar velórios por cemitério"
              >
                <Button marginLeft={4} type="submit">
                  <FiSearch />
                </Button>
              </Tooltip>
              <Tooltip
                label="Adicionar novo velório."
                aria-label="Adicionar novo velório."
              >
                <Button onClick={onOpenCreateFunerals} marginLeft={4}>
                  <FiPlus />
                </Button>
              </Tooltip>{' '}
            </Flex>
          </Form>
        </Flex>
      </Flex>
    </>
  );
};

export default Cemeteries;
