import React from 'react';

import dynamic from 'next/dynamic';

import { Box } from '@chakra-ui/core';

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false });

const CHART_CONFIG = {
  series: [
    {
      color: '#F56565',
      name: 'Despesas totais',
      data: [2.88, 1.59, 3.4, 2.87, 2.81, 4.87, 5.1, 3.69, 1.36],
    },
    {
      color: '#4299E1',
      name: 'Despesas realizadas',
      data: [76, 85, 101, 98, 87, 105, 91, 114, 94],
    },
  ],
  options: {
    chart: {
      type: 'bar',
      height: 350,
    },
    plotOptions: {
      bar: {
        horizontal: false,
        columnWidth: '55%',
        endingShape: 'rounded',
      },
    },
    dataLabels: {
      enabled: false,
    },
    stroke: {
      show: true,
      width: 2,
      colors: ['transparent'],
    },
    xaxis: {
      categories: [
        'Feb',
        'Mar',
        'Apr',
        'May',
        'Jun',
        'Jul',
        'Aug',
        'Sep',
        'Oct',
      ],
    },
    yaxis: {
      title: {
        text: '$ (thousands)',
      },
    },
    fill: {
      opacity: 1,
    },
    tooltip: {
      y: {
        formatter(val) {
          return `$ ${val} thousands`;
        },
      },
    },
  },
};

const ApexBarChart: React.FC = () => {
  return (
    <Box
      width="100%"
      marginTop={6}
      borderRadius={8}
      padding={2}
      background="white"
    >
      <Chart
        options={CHART_CONFIG.options}
        series={CHART_CONFIG.series}
        type="bar"
        height={350}
      />
    </Box>
  );
};
export default ApexBarChart;
