import React from 'react';

import { Heading } from '@chakra-ui/core';

const Header: React.FC = ({ children }) => (
  <Heading
    size="lg"
    borderBottom="1px solid"
    borderBottomColor="gray.300"
    paddingBottom={2}
    marginBottom={4}
  >
    {children}
  </Heading>
);

export default Header;
