import React, { useCallback, useEffect, useRef, useState } from 'react';
import { FiSearch, FiPlus } from 'react-icons/fi';
import { Column } from 'react-table';

import { Box, Button, Flex, Tooltip } from '@chakra-ui/core';
import { FormHandles } from '@unform/core';
import { Form } from '@unform/web';

import Input from '@/components/Input';
import SEO from '@/components/SEO';
import Sidebar from '@/components/Sidebar';
import Table from '@/components/Table';
import Title from '@/components/Title';

import api from '../../services/api';

interface IFormData {
  name: string;
  url_cam: string;
}
// eslint-disable-next-line
interface Funerals {
  id: string;
  name: string;
  url_cam: string;
  cemetery: {
    name: string;
  };
}

const FUNERAL_TABLE_COLUMNS = [
  {
    Header: 'Nome',
    accessor: 'name',
  },
  {
    Header: 'Link da câmera',
    accessor: 'url_cam',
  },
  {
    Header: 'Cemitério',
    accessor: 'cemetery',
  },
] as Column[];

const Funerals: React.FC = () => {
  const formRef = useRef<FormHandles>(null);

  const [funerals, setFunerals] = useState<Funerals[]>([] as Funerals[]);

  useEffect(() => {
    api.get('funerals').then(response => {
      const funeralsResponse: Funerals[] = response.data;

      setFunerals(funeralsResponse);
    });
  }, []);

  return (
    <>
      <SEO
        title="Endor"
        image="og/boost.png"
        shouldExcludeTitleSuffix
        description="Fazer o registro de velórios na plataforma"
      />
      <Flex
        as="main"
        height="100vh"
        position="relative"
        backgroundColor="gray.800"
      >
        <Sidebar />

        <Flex
          paddingLeft={2}
          paddingTop={15}
          paddingRight={65}
          width="100%"
          height="100%"
          bg="gray.800"
          direction="column"
        >
          <Box width="100%" color="gray.200">
            <Title css={{ color: 'gray.200' }}>Velórios</Title>
          </Box>

          <Form
            onSubmit={() => {
              console.log('ok');
            }}
            ref={formRef}
          >
            <Flex>
              <Input
                placeholder="Pesquisar velórios por cemitério"
                name="deceased_search"
                containerProps={{ width: 300, height: 40.5 }}
              />
              <Tooltip
                label="Pesquisar velórios por cemitério"
                aria-label="Pesquisar velórios por cemitério"
              >
                <Button marginLeft={4} type="submit">
                  <FiSearch />
                </Button>
              </Tooltip>

              <Tooltip
                label="Adicionar novo velório."
                aria-label="Adicionar novo velório."
              >
                <Button marginLeft={4}>
                  <FiPlus />
                </Button>
              </Tooltip>
            </Flex>

            <Flex marginTop={6}>
              <Table columns={FUNERAL_TABLE_COLUMNS} data={funerals}></Table>
            </Flex>
          </Form>
        </Flex>
      </Flex>
    </>
  );
};

export default Funerals;
