import React from 'react';

import {
  Flex,
  Image,
  Menu,
  Button,
  MenuButton,
  Text,
  MenuItem,
  Avatar,
  MenuList,
} from '@chakra-ui/core';

const Header: React.FC = () => (
  <Flex
    position="fixed"
    paddingX={6}
    width="100%"
    height={65}
    marginBottom={65}
    backgroundColor="#f5f5f0"
    alignItems="center"
    justifyContent="space-between"
    zIndex={3000}
  >
    <Image
      src="http://lafengenharia.com.br/wp-content/uploads/2018/10/cropped-MARCA_LAFE_CHAPADA.png"
      width={110}
    />

    <Flex height="100%">
      <Button
        width="49.8%"
        backgroundColor="#f5f5f0"
        color="black"
        borderRadius={0}
        borderBottom="2px solid "
        borderColor="blue.500"
        color="White"
        _hover={{
          bg: 'gray.800',
          color: 'White',
        }}
        _focusWithin={{
          bg: 'gray.800',
          color: 'White',
        }}
        // onClick={handleChangeToCustomerScreen}
      >
        Clientes
      </Button>
      <Button
        width="49.8%"
        backgroundColor="gray.800"
        borderRadius={0}
        borderBottom="2px solid"
        borderColor="blue.500"
        color="White"
        _hover={{
          bg: 'gray.800',
          color: 'White',
        }}
        _focusWithin={{
          border: '0px ',
          bg: 'gray.800',
          color: 'White',
        }}
        // onClick={handleChangeToEmployeeScreen}
      >
        Funcionários
      </Button>
    </Flex>
    <Flex alignItems="center">
      <Text marginRight={2}>Nome do usuário</Text>
      <Menu>
        <MenuButton width={10} height={10} _focusWithin={{ outline: 0 }}>
          <Avatar size="sm" bg="blue.300" />
        </MenuButton>
        <MenuList>
          <MenuItem>Sair</MenuItem>
          <MenuItem>Alterar avatar</MenuItem>
        </MenuList>
      </Menu>
    </Flex>
  </Flex>
);

export default Header;
