import React, { useCallback, useRef } from 'react';
import { FiSearch, FiPlus } from 'react-icons/fi';
import { Column } from 'react-table';

import { Box, Button, Flex, Tooltip, useToast } from '@chakra-ui/core';
import { FormHandles } from '@unform/core';
import { Form } from '@unform/web';
import * as Yup from 'yup';

import Input from '@/components/Input';
import SEO from '@/components/SEO';
import Sidebar from '@/components/Sidebar';
import Table from '@/components/Table';
import Title from '@/components/Title';
import getValidationErrors from '@/utils/getValidationErrors';

import api from '../../services/api';

interface IFormData {
  name: string;
}

const CEMETERY_TABLE_COLUMNS = [
  {
    Header: 'Nome',
    accessor: 'name',
  },
] as Column[];

const Cemeteries: React.FC = () => {
  const formRef = useRef<FormHandles>(null);

  const toast = useToast();

  const handleSubmit = useCallback(async (data: IFormData, { reset }) => {
    try {
      formRef.current?.setErrors({});

      const schema = Yup.object().shape({
        name: Yup.string().required('Nome obrigatório'),
      });

      await schema.validate(data, { abortEarly: false });

      await api.post('cemeteries', data);

      toast({
        status: 'success',
        title: 'Cemitério criado com sucesso',
        position: 'top',
        duration: 3000,
      });

      reset();
    } catch (err) {
      if (err instanceof Yup.ValidationError) {
        const errors = getValidationErrors(err);

        formRef.current?.setErrors(errors);

        return;
      }

      toast({
        status: 'error',
        title: 'Erro ao registrar cemitério',
        description: 'Ocorreu um erro ao registrar cemitério, tente novamente.',
        position: 'top',
        duration: 5000,
      });
    }
  }, []);

  return (
    <>
      <SEO
        title="Endor"
        image="og/boost.png"
        shouldExcludeTitleSuffix
        description="Fazer o registro de cemitérios na plataforma"
      />
      <Flex
        as="main"
        height="100vh"
        position="relative"
        backgroundColor="gray.800"
      >
        <Sidebar />

        <Flex
          paddingLeft={2}
          paddingTop={15}
          paddingRight={65}
          width="100%"
          height="100%"
          bg="gray.800"
          direction="column"
        >
          <Box width="100%" color="gray.200">
            <Title css={{ color: 'gray.200' }}>Cemitérios</Title>
          </Box>

          <Form ref={formRef} onSubmit={handleSubmit}>
            <Flex height="100%">
              <Input
                name="name"
                placeholder="Nome"
                containerProps={{
                  width: 300,
                  height: 10,
                  border: '1px solid',
                  borderColor: 'gray.400',
                  bg: 'white',
                }}
              />

              <Tooltip
                label="Pesquisar cemitérios por nome"
                aria-label="Pesquisar cemitérios por nome"
              >
                <Button marginLeft={4} type="submit">
                  <FiSearch />
                </Button>
              </Tooltip>

              <Tooltip
                label="Registrar novo cemitério"
                aria-label="Registrar novo cemitério"
              >
                <Button marginLeft={4} type="submit">
                  <FiPlus />
                </Button>
              </Tooltip>
            </Flex>
            <Flex marginTop={6}>
              <Table columns={CEMETERY_TABLE_COLUMNS} data={cemeteries}></Table>
            </Flex>
          </Form>
        </Flex>
      </Flex>
    </>
  );
};

export default Cemeteries;
