import React from 'react';

import {
  Flex,
  Image,
  Menu,
  Button,
  MenuButton,
  Text,
  MenuItem,
  Avatar,
  MenuList,
} from '@chakra-ui/core';

const Header: React.FC = () => (
  <Flex
    position="fixed"
    paddingX={6}
    width="100%"
    height={65}
    marginBottom={65}
    backgroundColor="#f5f5f0"
    alignItems="center"
    justifyContent="space-between"
    zIndex={3000}
  >
    <Image
      src="http://lafengenharia.com.br/wp-content/uploads/2018/10/cropped-MARCA_LAFE_CHAPADA.png"
      width={110}
    />

    <Flex height="100%" width="50%" justifyContent="space-between">
      <Button
        height="100%"
        width="49.8%"
        backgroundColor="#f5f5f0"
        color="black"
        borderRadius={0}
        borderBottom="3px solid "
        borderColor="#f5f5f0"
        _hover={{
          bg: 'gray.200',
          color: 'black',
        }}
        _focusWithin={{
          bg: 'gray.800',
          color: 'black',
        }}
        // onClick={handleChangeToCustomerScreen}
      >
        Clientes
      </Button>
      <Button
        height="100%"
        width="49.8%"
        backgroundColor="#f5f5f0"
        borderRadius={0}
        borderBottom="3px solid"
        borderColor="blue.700"
        color="black"
        _hover={{
          bg: 'gray.200',
          color: 'black',
        }}
        _focusWithin={{
          border: '0px ',
          bg: 'gray.200',
          color: 'black',
        }}
        // onClick={handleChangeToEmployeeScreen}
      >
        Funcionários
      </Button>
    </Flex>
    <Flex alignItems="center">
      <Text marginRight={2}>Nome do usuário</Text>
      <Menu>
        <MenuButton width={10} height={10} _focusWithin={{ outline: 0 }}>
          <Avatar size="sm" bg="blue.300" />
        </MenuButton>
        <MenuList>
          <MenuItem>Sair</MenuItem>
          <MenuItem>Alterar avatar</MenuItem>
        </MenuList>
      </Menu>
    </Flex>
  </Flex>
);

export default Header;
