import React, { useState } from 'react';
import Chart, { Props as ChartProps } from 'react-apexcharts';

import { Box } from '@chakra-ui/core';

const ApexBarChart: React.FC = () => {
  const [chartConfigs, setChartConfigs] = useState({
    series: [
      {
        color: '#F56565',
        name: 'Despesas totais',
        data: [44, 55, 57, 56, 61, 58, 63, 60, 66],
      },
      {
        color: '#4299E1',
        name: 'Despesas realizadas',
        data: [76, 85, 101, 98, 87, 105, 91, 114, 94],
      },
    ],
    options: {
      chart: {
        borderRadius: '8px',
        background: '#fff',
        width: '100%',
        type: 'bar',
        height: 350,
      },
      plotOptions: {
        bar: {
          horizontal: false,
          columnWidth: '55%',
          endingShape: 'rounded',
        },
      },
      dataLabels: {
        enabled: false,
      },
      stroke: {
        show: true,
        width: 2,
        colors: ['transparent'],
      },
      xaxis: {
        categories: [
          'Feb',
          'Mar',
          'Apr',
          'May',
          'Jun',
          'Jul',
          'Aug',
          'Sep',
          'Oct',
        ],
      },
      yaxis: {
        title: {
          text: '% porcentagem',
        },
      },
      fill: {
        opacity: 1,
      },
      tooltip: {
        y: {
          formatter(val) {
            return `$ ${val} thousands`;
          },
        },
      },
    },
  });

  return (
    <Box
      width="100%"
      marginTop={6}
      borderRadius={8}
      padding={2}
      background="white"
    >
      <Chart
        options={chartConfigs.options}
        series={chartConfigs.series}
        type="bar"
        height={350}
      />
    </Box>
  );
};
export default ApexBarChart;
