import React, { useCallback, useEffect, useRef, useState } from 'react';
import { FiSearch, FiUserPlus } from 'react-icons/fi';

import { Box, Button, Flex, useToast, Tooltip } from '@chakra-ui/core';
import { FormHandles } from '@unform/core';
import { Form } from '@unform/web';
import * as Yup from 'yup';

import Input from '@/components/Input';
import Select from '@/components/Select';
import SEO from '@/components/SEO';
import Sidebar from '@/components/Sidebar';
import Title from '@/components/Title';
import getValidationErrors from '@/utils/getValidationErrors';

import api from '../../services/api';

interface IFormData {
  name: string;
  url_cam: string;
}
// eslint-disable-next-line
interface Cemeteries {
  id: string;
  name: string;
}

const Funerals: React.FC = () => {
  const formRef = useRef<FormHandles>(null);

  const [cemeteries, setCemeteries] = useState<Cemeteries[]>(
    [] as Cemeteries[],
  );
  const toast = useToast();

  useEffect(() => {
    api.get('cemeteries').then(response => {
      const cemeteriesResponse: Cemeteries[] = response.data;

      setCemeteries(cemeteriesResponse);
    });
  }, []);

  const handleSubmit = useCallback(async (data: IFormData, { reset }) => {
    try {
      formRef.current?.setErrors({});

      const schema = Yup.object().shape({
        name: Yup.string().required('Nome obrigatório'),
        url_cam: Yup.string().required('Link da camêra obrigatório'),
        cemetery_id: Yup.string().uuid().required('Cemitério obrigatório'),
      });

      await schema.validate(data, { abortEarly: false });

      await api.post('funerals', data);

      toast({
        status: 'success',
        title: 'Velório criado com sucesso',
        position: 'top',
        duration: 3000,
      });

      reset();
    } catch (err) {
      if (err instanceof Yup.ValidationError) {
        const errors = getValidationErrors(err);

        formRef.current?.setErrors(errors);

        return;
      }

      toast({
        status: 'error',
        title: 'Erro ao registrar cemitério',
        description: 'Ocorreu um erro ao registrar cemitério, tente novamente.',
        position: 'top',
        duration: 5000,
      });
    }
  }, []);

  return (
    <>
      <SEO
        title="Endor"
        image="og/boost.png"
        shouldExcludeTitleSuffix
        description="Fazer o registro de velórios na plataforma"
      />
      <Flex
        as="main"
        height="100vh"
        position="relative"
        backgroundColor="gray.800"
      >
        <Sidebar />

        <Flex
          paddingLeft={2}
          paddingTop={15}
          paddingRight={65}
          width="100%"
          height="100%"
          bg="gray.800"
          direction="column"
        >
          <Box width="100%" color="gray.200">
            <Title css={{ color: 'gray.200' }}>Velórios</Title>
          </Box>

          <Form
            onSubmit={() => {
              console.log('ok');
            }}
            ref={formRef}
          >
            <Flex>
              <Input
                placeholder="Pesquisar falecidos por nome"
                name="deceased_search"
                containerProps={{ width: 300, height: 40.5 }}
              />
              <Tooltip
                label="Pesquisar falecidos por nome"
                aria-label="Pesquisar falecidos por nome"
              >
                <Button marginLeft={4} type="submit">
                  <FiSearch />
                </Button>
              </Tooltip>

              <Tooltip
                label="Adicionar novo falecido."
                aria-label="Adicionar novo falecido."
              >
                <Button marginLeft={4}>
                  <FiUserPlus />
                </Button>
              </Tooltip>
            </Flex>
          </Form>
        </Flex>
      </Flex>
    </>
  );
};

export default Funerals;
