import React, { useEffect } from 'react';
import { Props as ApexChartProps } from 'react-apexcharts';

import dynamic from 'next/dynamic';

import { Box } from '@chakra-ui/core';

const Chart = dynamic(() => import('react-apexcharts'), { ssr: false });

interface IBarChartProps extends ApexChartProps {
  bars: Array<{
    color: string;
    name: string;
    data: number[];
  }>;
}

const ApexBarChart: React.FC = ({ bars }: IBarChartProps) => {
  useEffect(() => {
    const config = {
      series: bars,
      options: {
        chart: {
          type: 'bar',
          height: 350,
        },
        plotOptions: {
          bar: {
            horizontal: false,
            columnWidth: '55%',
            endingShape: 'rounded',
          },
        },
        dataLabels: {
          enabled: false,
        },
        stroke: {
          show: true,
          width: 2,
          colors: ['transparent'],
        },
        xaxis: {
          categories: [
            'Feb',
            'Mar',
            'Apr',
            'May',
            'Jun',
            'Jul',
            'Aug',
            'Sep',
            'Oct',
          ],
        },
        yaxis: {
          title: {
            text: '$ (thousands)',
          },
        },
        fill: {
          opacity: 1,
        },
        tooltip: {
          y: {
            formatter(val) {
              return `$ ${val} thousands`;
            },
          },
        },
      },
    };
  }, []);

  return (
    <Box
      width="100%"
      marginTop={6}
      borderRadius={8}
      padding={2}
      background="white"
    >
      <Chart
        options={CHART_CONFIG.options}
        series={CHART_CONFIG.series}
        type="bar"
        height={350}
      />
    </Box>
  );
};
export default ApexBarChart;
