import React, { useEffect, useRef, useState } from 'react';
import { FiSearch } from 'react-icons/fi';
import { Column } from 'react-table';

import BarChart from '@/components/BarChart';
import DashedChart from '@/components/DashedChart';
import Header from '@/components/Header';
import Select from '@/components/Select';
import SEO from '@/components/SEO';
import Sidebar from '@/components/Sidebar';
import Table from '@/components/Table';
import Title from '@/components/Title';
import { Box, Button, Flex, Text, Tooltip } from '@chakra-ui/core';
import { FormHandles } from '@unform/core';
import { Form } from '@unform/web';

import directExpenses from '../mocks/macro-dashboard/33/charts/direct-expenses';
import totalExpenses from '../mocks/macro-dashboard/33/charts/total-expenses';
import general from '../mocks/macro-dashboard/33/tables/general';
import aheadActivities from '../mocks/macro-dashboard/33/tables/summary-of-main-activities/ahead-activities';
import lateActivities from '../mocks/macro-dashboard/33/tables/summary-of-main-activities/late-activities';

interface IFormData {
  email: string;
  password: string;
}

interface IMainActivities {
  item: string;
  activity: string;
  prev: string;
  real: string;
  physical_deviation: string;
}

interface IChartData {
  bars: Array<{
    color: string;
    name: string;
    data: number[];
  }>;
  categories: Array<string>;
}

interface IBuild {
  directExpenses: IChartData;
  totalExpenses: IChartData;
  general: Array<object>;
  aheadActivities: IMainActivities[];
  lateActivities: IMainActivities[];
}

const GENERAL = [
  {
    Header: <Text color="red.500">Mês</Text>,
    accessor: 'month',
  },
  {
    Header: <Text color="blue.500">% Físico acumulado previsto</Text>,
    accessor: 'estimated_cumultative_physics',
  },
  {
    Header: <Text color="red.500">% Físico Acumulado Executado</Text>,
    accessor: 'cumulative_physics_executed',
  },
  {
    Header: <Text color="blue.500">% Físico Mensal Previsto</Text>,
    accessor: 'expected_monthly_physical',
  },
  {
    Header: <Text color="red.500">% Físico Mensal Executado</Text>,
    accessor: 'physical_monthly_executed',
  },
  {
    Header: 'Desvio físico',
    accessor: 'physical_deviation',
  },
  {
    Header: <Text color="green.500">Obra adiantada (em dias)</Text>,
    accessor: 'performed_monthly_physicist',
  },
] as Column[];

const SUMMARY_OF_AHEAD_ACTIVITIES = [
  {
    Header: <Text color="blue.500">Item</Text>,
    accessor: 'item',
  },
  {
    Header: <Text color="blue.500">Atividades Adiantadas</Text>,
    accessor: 'activity',
  },
  {
    Header: <Text color="blue.500">% Prev. Até a data</Text>,
    accessor: 'prev',
  },
  {
    Header: <Text color="blue.500">% Real. Até a data</Text>,
    accessor: 'real',
  },
] as Column[];

const SUMMARY_OF_LATE_ACTIVITIES = [
  {
    Header: <Text color="red.500">Item</Text>,
    accessor: 'item',
  },
  {
    Header: <Text color="red.500">Atividades Atrasadas</Text>,
    accessor: 'activity',
  },
  {
    Header: <Text color="red.500">% Prev. Até a data</Text>,
    accessor: 'prev',
  },
  {
    Header: <Text color="red.500">% Real. Até a data</Text>,
    accessor: 'real',
  },
] as Column[];

const Dashboad: React.FC = () => {
  const formRef = useRef<FormHandles>(null);

  const [selectedBuild, setSelectedBuild] = useState<IBuild>({
    directExpenses,
    totalExpenses,
    general,
    aheadActivities,
    lateActivities,
  });

  return (
    <>
      <SEO
        title="Lafe"
        image="og/boost.png"
        shouldExcludeTitleSuffix
        description="Fazer Dashboad na plataforma"
      />

      <Header />

      <Sidebar />

      <Box
        as="main"
        maxWidth="100vw"
        paddingLeft="7rem"
        paddingRight={{ xs: 12, lg: 8 }}
        paddingTop={20}
      >
        <Flex color="white" direction="column">
          <Title>Filtros</Title>

          <Form
            css={{
              width: '35%',
              display: 'flex',
              alignItems: 'center',
              flexDirection: 'row',
            }}
            ref={formRef}
            onSubmit={() => {}}
          >
            <Select
              height={8}
              backgroundColor="white"
              name="construction"
              placeholder="Selecione a obra"
              containerProps={{
                border: '1px solid',
                height: 10,
                borderColor: 'gray.400',
                bg: 'white',
              }}
            >
              <option value="Obra1">Obra1</option>
              <option value="Obra2">Obra2</option>
              <option value="Obra3">Obra3</option>
            </Select>

            <Select
              height={8}
              backgroundColor="white"
              name="month"
              placeholder="Selecione o mês"
              containerProps={{
                height: 10,
                marginLeft: '16px',
                border: '1px solid',
                borderColor: 'gray.400',
                bg: 'white',
              }}
            >
              <option value="Mês1">Mês1</option>
              <option value="Mês2">Mês2</option>
              <option value="Mês3">Mês3</option>
            </Select>

            <Tooltip label="Filtrar resultados" aria-label="Filtrar resultados">
              <Button marginLeft={4} height={10} type="submit">
                <FiSearch color="Black" size={40} />
              </Button>
            </Tooltip>
          </Form>
        </Flex>

        <Flex marginTop={6} display="grid" gridTemplateColumns="60% 40%">
          <Box>
            <Table data={general} columns={GENERAL} />
            <BarChart
              title="Dispesas Diretas - Previstas X Realizadas"
              data={directExpenses}
            />
            <DashedChart
              title="Dispesas Diretas - Previstas X Realizadas"
              data={directExpenses}
            />
          </Box>

          <Flex marginLeft={6} direction="column">
            <Flex
              direction="column"
              padding={4}
              backgroundColor="white"
              borderRadius="md"
              height="100%"
            >
              <Text
                textAlign="center"
                fontWeight="bold"
                fontSize={20}
                marginBottom={2}
              >
                Principais Atividades Resumo
              </Text>
              <Table
                marginBottom={6}
                title="Resumo das Principais Atividades"
                data={lateActivities}
                columns={SUMMARY_OF_LATE_ACTIVITIES}
              />

              <Table
                title="Principais Atividades Resumo"
                data={aheadActivities}
                columns={SUMMARY_OF_AHEAD_ACTIVITIES}
              />
            </Flex>
          </Flex>
        </Flex>
        <Flex marginTop={6} direction="column">
          <BarChart
            title="Despesas Totais (DD+DI) - Previstas x Realizadas"
            data={selectedBuild.totalExpenses}
          />
          <DashedChart
            title="Despesas Totais (DD+DI) - Previstas x Realizadas"
            data={selectedBuild.totalExpenses}
          />
        </Flex>
      </Box>
    </>
  );
};

export default Dashboad;
